package A3_Loops_Program;

public class a1_printnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i =1;i<=100;i++)
		{
			System.out.println(i);
		}
		
		int i1=1;
		while( i1<=100)
	
		{
			System.out.println(i1);
			i1++;
		}
		
		int i2=1;
		do
		{
			System.out.println(i2);
			i2++;
			
		}while(i2<=100);
	}

}
