package A3_Loops_Program;

public class a2_reverseprintnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i =100;i>=1;i--)
		{
			System.out.println(i);
		}
		
		int i1=100;
		while(i1>=1)
		{
			System.out.println(i1);
			i1--;
		}
		
		int i2=100;
		do
		{
			System.out.println(i2);
			i2--;
		}while(i2>=1);
	}

}
