package A3_Loops_Program;

public class a4_multiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

        int num = 5;   // Change number here

        for (int i = 1; i <= 10; i++) {
            System.out.println(num + " x " + i + " = " + (num * i));
            
            
         
        }
	}

}


